import{_ as e,o as a,c as t,f as d}from"./app.b44075e6.js";const m=JSON.parse('{"title":"\u6D45\u8C08\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00","link":"#\u524D\u8A00","children":[]},{"level":2,"title":"\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3","slug":"\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3","link":"#\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3","children":[]},{"level":2,"title":"\u5BA2\u6237\u7AEF\u6E32\u67D3","slug":"\u5BA2\u6237\u7AEF\u6E32\u67D3","link":"#\u5BA2\u6237\u7AEF\u6E32\u67D3","children":[]},{"level":2,"title":"\u670D\u52A1\u7AEF\u6E32\u67D3","slug":"\u670D\u52A1\u7AEF\u6E32\u67D3","link":"#\u670D\u52A1\u7AEF\u6E32\u67D3","children":[]},{"level":2,"title":"\u9759\u6001\u7AD9\u70B9\u751F\u6210","slug":"\u9759\u6001\u7AD9\u70B9\u751F\u6210","link":"#\u9759\u6001\u7AD9\u70B9\u751F\u6210","children":[]},{"level":2,"title":"\u6DF7\u5408\u6E32\u67D3","slug":"\u6DF7\u5408\u6E32\u67D3","link":"#\u6DF7\u5408\u6E32\u67D3","children":[]},{"level":2,"title":"\u8FB9\u7F18\u6E32\u67D3","slug":"\u8FB9\u7F18\u6E32\u67D3","link":"#\u8FB9\u7F18\u6E32\u67D3","children":[]},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3","link":"#\u603B\u7ED3","children":[]}],"relativePath":"blog/5-rendering-modes.md","lastUpdated":1679647338000}'),r={name:"blog/5-rendering-modes.md"},c=d('<h1 id="\u6D45\u8C08\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F" tabindex="-1">\u6D45\u8C08\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F <a class="header-anchor" href="#\u6D45\u8C08\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F" aria-hidden="true">#</a></h1><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u8FD1\u671F\uFF0CReact\u6B63\u5F0F\u53D1\u5E03\u4E86\u5168\u65B0\u7684React\u5B98\u65B9\u6587\u6863\u2014\u2014<a href="https://react.dev/" target="_blank" rel="noreferrer">react.dev</a>\u3002\u76EE\u524D\u53EA\u53D1\u5E03\u4E86\u82F1\u6587\u7248\uFF0C\u4E2D\u6587\u7248\u5E76\u672A\u4E0A\u7EBF\u3002</p><p>\u5728\u65B0\u6587\u6863\u4E2D\u6709\u4E24\u4E2A\u7279\u70B9\uFF1A</p><ol><li>\u5168\u9762\u62E5\u62B1<code>Hooks</code></li><li>\u5168\u9762\u62E5\u62B1<code>full-stack</code></li></ol><p>\u5168\u9762\u62E5\u62B1<code>Hooks</code>\u3002React\u65E7\u5B98\u65B9\u6587\u6863\u662F\u57FA\u4E8E<code>class component</code>\u8BB2\u89E3\uFF0C\u4F46\u81EA\u4ECE<code>v16.8</code>\u51FA\u73B0<code>Hooks</code>\u540E\uFF0C<code>Hooks</code>\u9010\u6E10\u6210\u4E3A\u4E86React\u4E3B\u8981\u53D1\u5C55\u65B9\u5411\uFF0C\u5E76\u4E14\u65B0\u7279\u6027\u4E5F\u662F\u57FA\u4E8E<code>Hooks</code>\u5F00\u53D1\uFF0C\u6240\u4EE5\u65B0\u6587\u6863\u5168\u9762\u62E5\u62B1<code>Hooks</code>\u662F\u5927\u52BF\u6240\u8D8B\u3002</p><p>\u5168\u9762\u62E5\u62B1<code>full-stack</code>\u3002\u5728\u65E7\u6587\u6863\u4E2D\u63A8\u8350\u4F7F\u7528<code>create-react-app</code>\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684React\u9879\u76EE\uFF0C\u4F46\u5728\u65B0\u6587\u6863\u4E2D\u63A8\u8350\u4F7F\u7528<a href="https://nextjs.org/" target="_blank" rel="noreferrer">Next.js</a>\u6216<a href="https://remix.run/" target="_blank" rel="noreferrer">Remix</a>\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u9879\u76EE\u3002\u56E0\u6B64\uFF0C\u9010\u6E10\u5411<code>full-stack</code>\u8F6C\u53D8\u662F\u524D\u7AEF\u53D1\u5C55\u7684\u4E00\u4E2A\u8D8B\u52BF\u3002</p><p>\u5411<code>full-stack</code>\u7684\u8F6C\u53D8\u8FC7\u7A0B\u4E2D\uFF0C\u6709\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u6539\u53D8\u5C31\u662F\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F\u7684\u53D8\u5316\u3002\u901A\u8FC7<code>create-react-app</code>\u6240\u521B\u5EFA\u7684\u9879\u76EE\u5C5E\u4E8E\u5355\u9875\u9762\u5E94\u7528\u2014\u2014<code>SPA</code>\uFF0C\u5176\u6E32\u67D3\u6A21\u5F0F\u4E3A\u5BA2\u6237\u7AEF\u6E32\u67D3\u2014\u2014<code>CSR</code>\u3002\u901A\u8FC7Next.js\u6216Remix\u521B\u5EFA\u7684\u9879\u76EE\u7684\u6E32\u67D3\u6A21\u5F0F\u4E3B\u8981\u4E3A\u670D\u52A1\u7AEF\u6E32\u67D3\u2014\u2014<code>SSR</code>\u3002\u90A3\u4E48\uFF0C\u4E8C\u8005\u6E32\u67D3\u6A21\u5F0F\u6709\u4EC0\u4E48\u5DEE\u5F02\uFF1F\u8FD8\u6709\u5176\u4ED6\u7684\u6E32\u67D3\u6A21\u5F0F\u5417\uFF1F\u63A5\u4E0B\u6765\uFF0C\u8BA9\u6211\u4EEC\u5BF9\u4E8E\u524D\u7AEF\u6E32\u67D3\u6A21\u5F0F\u53CA\u5176\u53D1\u5C55\u8FDB\u884C\u5206\u6790\u3002</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>React is a library. It lets you put components together, but it doesn\u2019t prescribe how to do routing and data fetching. To build an entire app with React, we recommend a full-stack React framework like Next.js or Remix.</p><p>--from react.dev</p></div><h2 id="\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3" tabindex="-1">\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3 <a class="header-anchor" href="#\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u5728\u8FC7\u53BB\u4F20\u7EDF\u5F00\u53D1\u4E2D\uFF0C\u9875\u9762\u6E32\u67D3\u4EFB\u52A1\u662F\u7531\u670D\u52A1\u7AEF\u5B8C\u6210\u7684\uFF0C\u670D\u52A1\u5668\u8D1F\u8D23\u83B7\u53D6\u6570\u636E\uFF0C\u62FC\u88C5\u9875\u9762\uFF0C\u5BA2\u6237\u7AEF\u4EC5\u8D1F\u8D23\u5185\u5BB9\u663E\u793A\uFF0C\u4F7F\u7528\u8FD9\u79CD\u65B9\u5F0F\u7684\u5178\u578B\u6280\u672F\u6709<code>JSP</code>\u3001<code>PHP</code>\u7B49\u7B49\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/dd42a6192a5f03ee.png" alt="\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3"></p><p>\u5F88\u663E\u7136\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u4E2D\uFF0C\u670D\u52A1\u5668\u7684\u4EFB\u52A1\u6BD4\u8F83\u7E41\u91CD\uFF0C\u670D\u52A1\u5668\u8D1F\u8F7D\u76F8\u5BF9\u8F83\u5927\uFF0C\u8FD9\u6837\u540C\u7B49\u60C5\u51B5\u4E0B\u4E00\u53F0\u673A\u5668\u80FD\u591F\u540C\u65F6\u8D1F\u8F7D\u7684\u7528\u6237\u6570\u5C31\u53D8\u5C11\u4E86\u3002\u53E6\u5916\u5F00\u53D1\u8FC7\u7A0B\u4E2D\uFF0C\u7531\u4E8E\u524D\u540E\u7AEF\u5185\u5BB9\u662F\u6DF7\u5728\u4E00\u8D77\u7684\uFF0C\u8FD9\u5C31\u8981\u6C42\u5F00\u53D1\u8005\u8981\u540C\u65F6\u638C\u63E1\u524D\u540E\u7AEF\u77E5\u8BC6\uFF0C\u8981\u6C42\u8F83\u9AD8\u3002\u5982\u679C\u662F\u7531\u524D\u540E\u7AEF\u5DE5\u7A0B\u5E08\u5408\u4F5C\u5F00\u53D1\uFF0C\u4E5F\u5BB9\u6613\u9020\u6210\u65F6\u95F4\u51B2\u7A81\uFF0C\u524D\u7AEF\u9875\u9762\u4E0D\u51FA\u6765\uFF0C\u540E\u7AEF\u5DE5\u7A0B\u5E08\u5C31\u8981\u5E72\u7B49\u7740\uFF0C\u5F00\u53D1\u6548\u7387\u4E0D\u9AD8\u3002\u4E8E\u662F\u540E\u6765\u4FBF\u6709\u4E86\u5927\u5BB6\u719F\u6089\u7684 Vue.js\u3001React \u7B49 SPA \u6846\u67B6\u5927\u884C\u5176\u9053\u3002</p><h2 id="\u5BA2\u6237\u7AEF\u6E32\u67D3" tabindex="-1">\u5BA2\u6237\u7AEF\u6E32\u67D3 <a class="header-anchor" href="#\u5BA2\u6237\u7AEF\u6E32\u67D3" aria-hidden="true">#</a></h2><p>Vue.js\u3001React \u8FD9\u7C7B\u6846\u67B6\u4E4B\u6240\u4EE5\u80FD\u89E3\u51B3\u524D\u9762\u63D0\u5230\u7684\u95EE\u9898\uFF0C\u4E3B\u8981\u662F\u56E0\u4E3A\u91C7\u7528\u4E86\u524D\u540E\u7AEF\u5206\u79BB\u7684\u5F00\u53D1\u6A21\u5F0F\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u7684\u7279\u70B9\u662F\u9875\u9762\u662F\u7531\u5BA2\u6237\u7AEF\u6E32\u67D3\u7684\uFF0C\u5E94\u7528\u5728\u5BA2\u6237\u7AEF\u9996\u6B21\u83B7\u53D6\u7684\u662F\u7A7A HTML \u6587\u6863\uFF0C\u6D4F\u89C8\u5668\u4E0B\u8F7D\u5E76\u8FD0\u884C JS\uFF0C\u83B7\u53D6\u6570\u636E\u4E4B\u540E\u518D\u521B\u5EFA\u9875\u9762\uFF0C\u4E5F\u5C31\u662F\u5927\u5BB6\u719F\u6089\u7684\u5355\u9875\u9762\u5E94\u7528 - SPA\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/93a6319a53acb528.png" alt="\u5BA2\u6237\u7AEF\u6E32\u67D3"></p><p>\u8FD9\u79CD\u65B9\u5F0F\uFF0C\u670D\u52A1\u7AEF\u4EC5\u63D0\u4F9B\u6570\u636E\u63A5\u53E3\uFF0C\u62FC\u88C5\u9875\u9762\u7684\u4EFB\u52A1\u4EA4\u7ED9\u4E86\u5BA2\u6237\u7AEF\uFF0C\u8FD9\u6837\u5145\u5206\u5229\u7528\u5BA2\u6237\u7AEF\u6027\u80FD\uFF0C\u63D0\u9AD8\u4E86\u670D\u52A1\u5668\u8D1F\u8F7D\u80FD\u529B\u3002\u5F00\u53D1\u8FC7\u7A0B\u4E2D\uFF0C\u524D\u540E\u7AEF\u5206\u79BB\uFF0C\u524D\u540E\u7AEF\u5DE5\u7A0B\u5E08\u5404\u53F8\u5176\u804C\uFF0C\u6709\u6548\u63D0\u5347\u5F00\u53D1\u6548\u7387\u3002</p><p>\u4F46\u540C\u65F6\uFF0C\u8FD9\u79CD\u6E32\u67D3\u6A21\u5F0F\u4E5F\u5B58\u5728\u7F3A\u70B9\uFF1A\u7528\u6237\u4E0D\u5F97\u4E0D\u7B49\u5F85\u6D4F\u89C8\u5668\u4E0B\u8F7D\u5E76\u6267\u884C JS \u4EE3\u7801\u624D\u80FD\u770B\u5230\u5185\u5BB9\uFF0C\u8FD9\u8DDF\u5BA2\u6237\u7AEF\u7F51\u901F\u548C\u786C\u4EF6\u6027\u80FD\u6709\u5173\uFF0C\u4F1A\u589E\u52A0\u9996\u5C4F\u52A0\u8F7D\u65F6\u95F4\uFF0C\u5F71\u54CD\u7528\u6237\u4F53\u9A8C\u3002\u540C\u65F6\uFF0C\u7531\u4E8E\u9875\u9762\u5185\u5BB9\u4E00\u5F00\u59CB\u4E3A\u7A7A\uFF0C\u4E5F\u4F1A\u5BFC\u81F4\u641C\u7D22\u5F15\u64CE\u65E0\u6CD5\u722C\u53D6\u7ED3\u679C\uFF0C\u4E0D\u5229\u4E8E SEO\u3002</p><p>\u56E0\u6B64\uFF0CCSR \u9002\u5408\u90A3\u4E9B\u4E0D\u9700\u8981 SEO \u6216\u8005\u7528\u6237\u8BBF\u95EE\u9891\u7387\u4E0D\u9AD8\u7684\u5F3A\u4EA4\u4E92\u5E94\u7528\uFF0C\u4F8B\u5982\uFF1ASaaS \u7CFB\u7EDF\u3001\u540E\u53F0\u7BA1\u7406\u7CFB\u7EDF\u3001\u5728\u7EBF\u6587\u6863\u7B49\u3002\u540C\u65F6\uFF0C\u5229\u7528\u6D4F\u89C8\u5668\u7F13\u5B58\u7279\u6027\uFF0C\u8FD9\u4E9B\u5E94\u7528\u4E5F\u53EF\u4EE5\u907F\u514D\u518D\u6B21\u4E0B\u8F7D\u8D44\u6E90\u4ECE\u800C\u63D0\u9AD8\u6027\u80FD\u8868\u73B0\u3002</p><h2 id="\u670D\u52A1\u7AEF\u6E32\u67D3" tabindex="-1">\u670D\u52A1\u7AEF\u6E32\u67D3 <a class="header-anchor" href="#\u670D\u52A1\u7AEF\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u524D\u9762\u63D0\u5230 SPA \u7684 SEO \u95EE\u9898\u548C\u9996\u5C4F\u52A0\u8F7D\u901F\u5EA6\u95EE\u9898\uFF0C\u6709\u4EC0\u4E48\u597D\u529E\u6CD5\u53EF\u4EE5\u89E3\u51B3\u5B83\u4EEC\u5462\uFF1F\u5176\u5B9E\u4F20\u7EDF\u7684\u670D\u52A1\u7AEF\u6E32\u67D3\u5C31\u884C\u3002\u4F46\u662F\u8FD9\u6837\u4E00\u6765\u5C82\u4E0D\u662F\u5F00\u5386\u53F2\u5012\u8F66\uFF0C\u515C\u515C\u8F6C\u8F6C\u53C8\u56DE\u53BB\u4E86\uFF1F</p><p>\u89E3\u51B3\u65B9\u6848\u4E5F\u4E0D\u96BE\uFF0C\u53EA\u9700\u5BF9\u4F20\u7EDF\u670D\u52A1\u7AEF\u6E32\u67D3\u6A21\u5F0F\u505A\u4E00\u4E9B\u6539\u8FDB\uFF1A</p><p>\u5F53\u7528\u6237\u8BF7\u6C42\u4E00\u4E2A URL \u65F6\uFF0C\u5148\u6309\u7167\u4F20\u7EDF\u6A21\u5F0F\u5728\u670D\u52A1\u7AEF\u6E32\u67D3\u5B8C\u6574 HTML \u9875\u9762\u7ED9\u6D4F\u89C8\u5668\uFF0C\u8FD9\u6837\u7528\u6237\u7ACB\u523B\u53EF\u4EE5\u83B7\u5F97\u9996\u5C4F\u5185\u5BB9\uFF1B \u4E0E\u6B64\u540C\u65F6\uFF0C\u5BA2\u6237\u7AEF\u4F1A\u52A0\u8F7D JS \u4EE3\u7801\uFF0C\u4F7F\u524D\u9762\u52A0\u8F7D\u7684\u9759\u6001\u5185\u5BB9\u91CD\u65B0\u53D8\u6210\u53EF\u4EA4\u4E92\u7684 SPA\uFF0C\u8FD9\u6837\u540E\u7EED\u7684\u9875\u9762\u5207\u6362\u5C31\u4E0D\u518D\u9700\u8981\u5237\u65B0\u548C\u91CD\u65B0\u83B7\u53D6\u9875\u9762\u5185\u5BB9\u4E86\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/368cb9a7015c9387.png" alt="\u670D\u52A1\u7AEF\u6E32\u67D3"></p><p>\u4E3A\u4E86\u4FDD\u8BC1\u524D\u7AEF\u7A0B\u5E8F\u5458\u80FD\u591F\u4F7F\u7528\u719F\u6089\u7684\u65B9\u5F0F\u7F16\u5199\u9875\u9762\uFF0C\u5373\u201C\u540C\u6784\u5F00\u53D1\u201D\uFF0C\u670D\u52A1\u7AEF\u6E32\u67D3\u65F6\uFF0CNext \u5B9E\u9645\u4E0A\u662F\u5728\u670D\u52A1\u5668\u4E0A\u6267\u884C React\uFF0C\u5C06\u6211\u4EEC\u7F16\u5199\u7684\u7EC4\u4EF6\u6E32\u67D3\u4E3A HTML \u5E76\u8FD4\u56DE\u5BA2\u6237\u7AEF\u3002\u5BA2\u6237\u7AEF\u6FC0\u6D3B\u65F6\u6267\u884C\u7684 JS \u5B9E\u9645\u4E0A\u4E5F\u662F React\uFF0C\u5B83\u4F1A\u91CD\u65B0\u63A5\u7BA1\u6587\u6863\uFF0C\u6062\u590D\u6570\u636E\u548C\u72B6\u6001\uFF0C\u4F7F\u9759\u6001\u9875\u9762\u53D8\u5F97\u53EF\u4EA4\u4E92\uFF0C\u8FD9\u4E00\u8FC7\u7A0B\u79F0\u4E3A\u201C\u6CE8\u6C34(hydration)\u201D\u3002</p><p>\u53EF\u4EE5\u770B\u5230\uFF0CSSR \u4FDD\u7559 CSR \u4F18\u70B9\u7684\u540C\u65F6\uFF0C\u8FD8\u7ED9\u7528\u6237\u63D0\u4F9B\u4E86\u5FEB\u901F\u52A0\u8F7D\u9996\u5C4F\u7684\u80FD\u529B\uFF0C\u8FD9\u540C\u65F6\u4E5F\u89E3\u51B3\u4E86 SEO \u95EE\u9898\u3002</p><p>\u4F46\u8FD9\u79CD\u65B9\u5F0F\u4E5F\u5E76\u4E0D\u5B8C\u7F8E\uFF0C\u56E0\u4E3A\u670D\u52A1\u5668\u548C\u6D4F\u89C8\u5668\u73AF\u5883\u6709\u5DEE\u5F02\uFF0C\u5B83\u4EEC\u4E0D\u80FD\u7ED9\u5F00\u53D1\u8005\u63D0\u4F9B\u76F8\u540C API\uFF0C\u4F8B\u5982\uFF0C\u7EC4\u4EF6\u5728\u670D\u52A1\u7AEF\u521B\u5EFA\u65F6\u5C31\u6CA1\u6709 mounted \u94A9\u5B50\uFF0C\u56E0\u4E3A\u6839\u672C\u6CA1\u6709\u6302\u8F7D\u8FD9\u4E00\u6B65\uFF0C\u8FD9\u5BFC\u81F4\u4E0D\u5C11\u7EC4\u4EF6\u5E93\u4E0D\u80FD\u5728\u670D\u52A1\u7AEF\u73AF\u5883\u6B63\u5E38\u4F7F\u7528\u3002</p><p>\u53C8\u6BD4\u5982\uFF0C\u6709\u65F6\u5019\u6211\u4EEC\u7684\u4EE3\u7801\u53EA\u60F3\u5728\u5BA2\u6237\u7AEF\u8FD0\u884C\uFF0C\u5982\u679C\u4E0D\u5C0F\u5FC3\u5728\u670D\u52A1\u7AEF\u6267\u884C\u4E86\u5C31\u4F1A\u62A5\u9519\u3002\u8FD9\u4E9B\u90FD\u7ED9\u5F00\u53D1\u5E26\u6765\u4E86\u590D\u6742\u5EA6\u548C\u7EA6\u675F\u6027\u3002\u540C\u6837\u7684\uFF0C\u7531\u4E8E\u9875\u9762\u6E32\u67D3\u64CD\u4F5C\u53D1\u751F\u5728\u670D\u52A1\u7AEF\uFF0C\u6240\u4EE5\u670D\u52A1\u5668\u8D1F\u8F7D\u589E\u52A0\u4E86\uFF0C\u53EA\u662F\u7531\u4E8E\u540E\u7EED hydration \u64CD\u4F5C\uFF0C\u8FD9\u4E2A\u8D1F\u8F7D\u589E\u52A0\u91CF\u88AB\u5927\u5E45\u51CF\u5C11\u4E86\u3002\u6700\u540E\uFF0C\u5927\u5BB6\u8FD8\u5E94\u8BE5\u6CE8\u610F\u7684\u662F\uFF0CSSR \u8FD8\u989D\u5916\u589E\u52A0\u4E86 node.js / serverless \u8FD9\u6837\u7684\u8FD0\u884C\u65F6\u9700\u6C42\uFF0C\u8FD9\u5B9E\u9645\u4E0A\u4E5F\u662F\u4E00\u7B14\u8D39\u7528\u3002</p><p>\u7EFC\u4E0A\u6240\u8FF0\uFF0CSSR \u65B9\u5F0F\u975E\u5E38\u7075\u6D3B\uFF0C\u51E0\u4E4E\u53EF\u4EE5\u9002\u914D\u5927\u591A\u6570\u9700\u6C42\uFF0C\u5C24\u5176\u662F\u4E00\u4E9B\u57FA\u4E8E\u5185\u5BB9\u7684\u7F51\u7AD9\uFF1A\u535A\u5BA2\uFF0C\u7535\u5546\uFF0C\u5B98\u7F51\u7B49\u7B49\u3002</p><p>\u56E0\u6B64 Next \u9ED8\u8BA4\u6E32\u67D3\u6A21\u5F0F\u5C31\u662F SSR\u3002</p><h2 id="\u9759\u6001\u7AD9\u70B9\u751F\u6210" tabindex="-1">\u9759\u6001\u7AD9\u70B9\u751F\u6210 <a class="header-anchor" href="#\u9759\u6001\u7AD9\u70B9\u751F\u6210" aria-hidden="true">#</a></h2><p>\u6211\u7684\u535A\u5BA2\u4FBF\u662F\u4E00\u4E2A\u9759\u6001\u7AD9\u70B9\u751F\u6210\u7684\u9879\u76EE\uFF0C\u5927\u5BB6\u53EF\u4EE5\u770B\u4E00\u4E0B\u9879\u76EE\u4E2D packages.json \u6587\u4EF6\uFF0C\u91CC\u9762\u63D0\u4F9B\u4E86\u4E00\u4E2A build \u547D\u4EE4\u3002\u8FD9\u4E2A\u547D\u4EE4\u7684\u4F5C\u7528\u662F\u5C06\u5199\u597D\u7684\u52A8\u6001\u9875\u9762\u57FA\u4E8E\u6570\u636E\u751F\u6210\u4E3A\u9759\u6001\u7F51\u7AD9\uFF0C\u8FD9\u4E00\u8FC7\u7A0B\u79F0\u4E3A\u9759\u6001\u5185\u5BB9\u751F\u6210 - Static Site Generate\uFF0C\u5373 SSG\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/cbd6d93d4c135a4e.png" alt="\u9759\u6001\u7AD9\u70B9\u751F\u6210"></p><p>\u4E3A\u4EC0\u4E48\u8981\u505A\u9759\u6001\u5185\u5BB9\u751F\u6210\uFF1F\u7B54\u6848\u4E5F\u5F88\u7B80\u5355\uFF0C\u56E0\u4E3A\u5982\u679C\u7528\u6237\u6BCF\u6B21\u8BBF\u95EE\u4E00\u4E2A\u76F8\u540C\u7684\u9875\u9762\u90FD\u8981\u91CD\u65B0\u6E32\u67D3\u4E00\u6B21\u76F8\u540C\u7684\u5185\u5BB9\uFF0C\u65E0\u7591\u662F\u5728\u6D6A\u8D39\u8D44\u6E90\uFF0C\u6BD4\u5982\u4E00\u7BC7\u535A\u5BA2\u6216\u8005\u4E00\u4E2A\u5546\u54C1\u8BE6\u60C5\u9875\uFF0C\u7F16\u8F91\u5B8C\u6210\u4E4B\u540E\u5B83\u4EEC\u901A\u5E38\u4E0D\u600E\u4E48\u53D8\u52A8\uFF0C\u8FD9\u5C31\u6CA1\u6709\u5FC5\u8981\u6BCF\u6B21\u91CD\u65B0\u6E32\u67D3\uFF0C\u5982\u679C\u63D0\u524D\u6E32\u67D3\u597D\u4E00\u4E2A\u9759\u6001\u9875\uFF0C\u5C06\u4F1A\u83B7\u5F97\u6700\u5C0F\u7684\u670D\u52A1\u5668\u8D1F\u8F7D\uFF0C\u6700\u597D\u7684\u6027\u80FD\u8868\u73B0\uFF0C\u540C\u65F6\u4E5F\u51CF\u5C11\u7F51\u7AD9\u7684\u5B89\u5168\u98CE\u9669\u3002</p><p>\u540C\u65F6\uFF0C\u670D\u52A1\u5668\u9700\u6C42\u4E5F\u662F\u6700\u5C11\u7684\uFF0C\u6211\u4EEC\u4EC5\u9700\u8981\u9759\u6001\u7F51\u7AD9\u670D\u52A1\u5373\u53EF\uFF0C\u8FD9\u663E\u7136\u53EF\u4EE5\u5927\u5E45\u964D\u4F4E\u670D\u52A1\u5668\u6210\u672C\u3002\u5982\u679C\u6211\u4EEC\u53EA\u662F\u5F00\u53D1\u4E00\u4E2A\u4E2A\u4EBA\u7F51\u7AD9\u6216\u535A\u5BA2\uFF0C\u751A\u81F3\u53EF\u4EE5\u901A\u8FC7 github page\u3001vercel \u7B49\u9759\u6001\u670D\u52A1\u96F6\u6210\u672C\u5EFA\u7AD9\u3002</p><p>\u5F53\u7136 SSG \u4E5F\u5B58\u5728\u4E00\u4E9B\u95EE\u9898\uFF0C\u6BD4\u5982\u7F51\u7AD9\u5185\u5BB9\u4E00\u65E6\u53D8\u5316\uFF0C\u6211\u4EEC\u5C31\u9700\u8981\u91CD\u65B0\u751F\u6210\u6574\u7AD9\uFF0C\u7F51\u7AD9\u5185\u5BB9\u6BD4\u8F83\u5C11\u65F6\u8FD9\u5E76\u6CA1\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u5982\u679C\u5185\u5BB9\u6BD4\u8F83\u5E9E\u5927\uFF0C\u5219\u6BCF\u6B21\u91CD\u65B0\u751F\u6210\u7684\u65F6\u95F4\u6210\u672C\u5C31\u592A\u5927\u4E86\u3002\u56E0\u6B64\u4E5F\u5C31\u6709\u4E86\u589E\u91CF\u5185\u5BB9\u751F\u6210 - ISG\uFF0C\u5F53\u7136\u8FD9\u5C31\u662F\u53E6\u4E00\u4E2A\u8BDD\u9898\u4E86\u3002</p><p>\u6240\u4EE5\uFF0CSSG \u4EC5\u9002\u5408\u5185\u5BB9\u521B\u5EFA\u4E4B\u540E\u4E0D\u7ECF\u5E38\u53D8\u5316\u7684\u7F51\u7AD9\uFF0C\u4F8B\u5982\uFF1A\u4E2A\u4EBA\u535A\u5BA2\u3001\u7F51\u7AD9\u3001\u5BA3\u4F20\u9875\u7B49\u3002</p><h2 id="\u6DF7\u5408\u6E32\u67D3" tabindex="-1">\u6DF7\u5408\u6E32\u67D3 <a class="header-anchor" href="#\u6DF7\u5408\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u4E4B\u524D\u7684 Next \u7248\u672C\u4E2D\uFF0C\u4E00\u4E2A\u5E94\u7528\u53EA\u80FD\u4F7F\u7528\u4E00\u79CD\u6E32\u67D3\u6A21\u5F0F\uFF0C\u8981\u4E48\u5BA2\u6237\u7AEF\u6E32\u67D3\uFF0C\u8981\u4E48\u670D\u52A1\u7AEF\u6E32\u67D3\uFF0C\u8FD9\u79CD\u975E\u9ED1\u5373\u767D\u7684\u65B9\u5F0F\u4E0D\u7075\u6D3B\uFF0C\u4E00\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u5E94\u7528\u7684\u4E0D\u540C\u90E8\u5206\u7528\u4E0D\u540C\u7684\u6E32\u67D3\u65B9\u5F0F\u66F4\u9002\u5408\u3002\u6BD4\u5982\u7535\u5546\u5E94\u7528\u9996\u9875\u7ECF\u5E38\u53D8\u52A8\uFF0C\u9002\u5408\u670D\u52A1\u7AEF\u6E32\u67D3\uFF0C\u5546\u54C1\u8BE6\u60C5\u9875\u5219\u5E0C\u671B\u9759\u6001\u5316\uFF1B\u53C8\u6BD4\u5982 CMS \u4E2D admin \u90E8\u5206\u4E0D\u9700\u8981 SEO\uFF0C\u5219\u9002\u5408\u5BA2\u6237\u7AEF\u6E32\u67D3\uFF0C\u800C\u6240\u6709\u5185\u5BB9\u9875\u5219\u4EC5\u9700\u8981\u751F\u6210\u4E00\u6B21\u3002</p><p>Next \u5E26\u6765\u4E00\u4E2A\u65B0\u7684\u6E32\u67D3\u6A21\u5F0F\uFF0C\u79F0\u4E3A\u6DF7\u5408\u6E32\u67D3 - hybrid rendering\uFF0C\u987E\u540D\u601D\u4E49\uFF0C\u8FD9\u662F\u4E00\u79CD\u6839\u636E\u4E0D\u540C\u8DEF\u7531\u89C4\u5219\u4F7F\u7528\u4E0D\u540C\u65B9\u5F0F\u6E32\u67D3\u7684\u6A21\u5F0F\u3002\u8FD9\u79CD\u65B9\u5F0F\u5C31\u53EF\u4EE5\u7528\u6765\u89E3\u51B3\u524D\u9762\u63D0\u51FA\u7684\u9700\u6C42\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/e9d706cb1ccc5b99.png" alt="\u6DF7\u5408\u6E32\u67D3"></p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u9879\u76EE\u9700\u6C42\u4F7F\u7528\u4E00\u79CD\u6E32\u67D3\u6A21\u5F0F\u4E0D\u80FD\u6EE1\u8DB3\u65F6\uFF0C\u5C31\u53EF\u4EE5\u8003\u8651\u6DF7\u5408\u6E32\u67D3\u6A21\u5F0F\u3002</p><h2 id="\u8FB9\u7F18\u6E32\u67D3" tabindex="-1">\u8FB9\u7F18\u6E32\u67D3 <a class="header-anchor" href="#\u8FB9\u7F18\u6E32\u67D3" aria-hidden="true">#</a></h2><p>\u8FC7\u53BB\uFF0CSSR \u53EA\u80FD\u8FD0\u884C\u5728 Node.js \u73AF\u5883\uFF0C\u4F46\u662F Next \u63D0\u4F9B\u4E86\u8DE8\u5E73\u53F0\u652F\u6301\uFF0C\u80FD\u591F\u540C\u65F6\u8FD0\u884C\u5728 Node.js\u3001Deno\u3001Workers \u7B49\u8FD0\u884C\u65F6\u73AF\u5883\u3002</p><p>\u8FD9\u5C31\u7ED9\u7528\u6237\u5E26\u6765\u4E00\u79CD\u5168\u65B0\u4F7F\u7528\u65B9\u5F0F\uFF1A\u8FB9\u7F18\u6E32\u67D3 - edge-side rendering\uFF0C\u8FD9\u79CD\u65B9\u5F0F\u80FD\u591F\u5728 CDN edge worker \u73AF\u5883\u4E0B\u76F4\u63A5\u6267\u884C\u6E32\u67D3\uFF0C\u8FD9\u6837 Next \u5E94\u7528\u80FD\u591F\u8FD0\u884C\u5728\u79BB\u7528\u6237\u66F4\u8FD1\u7684\u73AF\u5883\u4E2D\uFF0C\u4ECE\u800C\u964D\u4F4E\u5EF6\u8FDF\u548C\u670D\u52A1\u5668\u82B1\u9500\u3002</p><p><img src="https://s3.bmp.ovh/imgs/2023/03/24/6bee8d1bcad10f69.png" alt="\u8FB9\u7F18\u6E32\u67D3"></p><p>\u56E0\u6B64\uFF0C\u5982\u679C\u5927\u5BB6\u9700\u8981\u6DF1\u5EA6\u4F18\u5316\u5E94\u7528\u6253\u5F00\u548C\u4EA4\u4E92\u901F\u5EA6\uFF0C\u4F8B\u5982\uFF1A\u5B9E\u65F6\u6E38\u620F\uFF0C\u4EA4\u6613\u7CFB\u7EDF\uFF0C\u5C31\u53EF\u4EE5\u5C1D\u8BD5\u8FB9\u7F18\u6E32\u67D3\u6A21\u5F0F\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u90A3\u4E48\uFF0C\u6211\u4EEC\u603B\u7ED3\u4E00\u4E0B\u6E32\u67D3\u6A21\u5F0F\u7684\u4E00\u4E9B\u5DEE\u5F02\uFF1A</p><ul><li>\u5BA2\u6237\u7AEF\u6E32\u67D3\uFF1A\u5F00\u53D1\u901F\u5EA6\u5FEB\uFF0C\u8282\u7EA6\u670D\u52A1\u5668\u8D44\u6E90\uFF1B\u9996\u5C4F\u6162\uFF0CSEO \u4E0D\u53CB\u597D\uFF1B</li><li>\u670D\u52A1\u7AEF\u6E32\u67D3\uFF1A\u9996\u5C4F\u5FEB\uFF0CSEO \u53CB\u597D\uFF0C\u9002\u5E94\u6027\u5F3A\uFF1B\u5F00\u53D1\u7EA6\u675F\u5927\uFF0C\u670D\u52A1\u5668\u8D39\u7528\u9AD8\uFF1B</li><li>\u9759\u6001\u7AD9\u70B9\u751F\u6210\uFF1A\u9996\u5C4F\u6781\u5FEB\uFF0CSEO \u53CB\u597D\uFF0C\u670D\u52A1\u5668\u6210\u672C\u4F4E\uFF1B\u9002\u5E94\u6027\u5F31\uFF0C\u53EF\u7EF4\u62A4\u6027\u5DEE\uFF1B</li><li>\u6DF7\u5408\u6E32\u67D3\uFF1A\u6309\u9700\u6E32\u67D3\uFF0C\u9002\u5E94\u6027\u5F3A\uFF0C\u53EF\u7EF4\u62A4\u6027\u597D\uFF1B\u7A33\u5B9A\u6027\u3001\u53EF\u7528\u6027\u4E0D\u597D\uFF1B</li><li>\u8FB9\u7F18\u6E32\u67D3\uFF1A\u6027\u80FD\u597D\uFF0C\u670D\u52A1\u5668\u6210\u672C\u4F4E\uFF1B\u7A33\u5B9A\u6027\u3001\u53EF\u7528\u6027\u4E0D\u597D\u3002</li></ul><p>\u6700\u540E\uFF0C\u524D\u7AEF\u5728\u53D1\u5C55\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6E32\u67D3\u6A21\u5F0F\u4E5F\u5728\u4E0D\u65AD\u7684\u53D1\u5C55\uFF0C\u5176\u9875\u9762\u54CD\u5E94\u901F\u5EA6\u4E5F\u8D8A\u6765\u8D8A\u5FEB\u3002\u90A3\u4E48\u672A\u6765\u7684\u4E0B\u4E00\u4EE3\u524D\u7AEF\u6846\u67B6\uFF0C\u4E00\u5B9A\u4F1A\u5728\u8FB9\u7F18\u6E32\u67D3\u65B9\u5411\uFF0C\u6216\u8005\u5728\u9759\u6001\u7AD9\u70B9\u751F\u6210\u4E2D\u589E\u91CF\u5185\u5BB9\u751F\u6210\u65B9\u5411\u505A\u6587\u7AE0\u3002</p>',51),i=[c];function o(p,l,s,n,h,S){return a(),t("div",null,i)}const u=e(r,[["render",o]]);export{m as __pageData,u as default};
