import{_ as e,o as p,c as a,f as t}from"./app.7ebbe92d.js";const m=JSON.parse('{"title":"\u6D45\u8C08\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D","description":"","frontmatter":{},"headers":[],"relativePath":"blog/news-is-dead.md","lastUpdated":1688640992000}'),s={name:"blog/news-is-dead.md"},_=t('<h1 id="\u6D45\u8C08\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D" tabindex="-1">\u6D45\u8C08\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D <a class="header-anchor" href="#\u6D45\u8C08\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D" aria-hidden="true">#</a></h1><p>\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D\uFF0C\u8FD9\u56DB\u4E2A\u5B57\u5B57\u9762\u4E4B\u610F\u5982\u662F\u3002</p><p>\u65B0\u95FB\uFF0C\u5176\u672C\u8D28\u5728\u4E8E\u771F\u5B9E\u3001\u7B80\u660E\u548C\u53CA\u65F6\u3002\u7136\u800C\uFF0C\u5728\u5F53\u4ECA\u65F6\u4EE3\uFF0C\u65B0\u95FB\u7684\u6700\u663E\u8457\u7279\u70B9\u5E76\u975E\u4EE5\u4E0A\u6240\u8FF0\uFF0C\u800C\u662F\u589E\u52A0\u4E86\u4E00\u4E2A\u65B0\u7684\u6210\u5206\u2014\u2014\u9009\u62E9\u6027\u3002\u5A92\u4F53\u62E5\u6709\u9009\u62E9\u6743\uFF0C\u4E14\u5177\u6709\u89E3\u91CA\u6743\uFF0C\u51B3\u5B9A\u65B0\u95FB\u662F\u5426\u62A5\u9053\uFF0C\u4EE5\u53CA\u5982\u4F55\u62A5\u9053\u3001\u62A5\u9053\u591A\u5C11\u3001\u4F55\u65F6\u62A5\u9053\uFF0C\u6216\u8005\u662F\u5426\u63A9\u76D6\u8FD8\u662F\u5EF6\u8FDF\u62A5\u9053\uFF0C\u8FD9\u90FD\u662F\u6709\u95E8\u8DEF\u5728\u91CC\u9762\u7684\u3002\u56E0\u6B64\uFF0C\u9009\u62E9\u6027\u6392\u5728\u9996\u4F4D\uFF0C\u771F\u5B9E\u6027\u548C\u7B80\u660E\u6027\u5219\u6E10\u6E10\u9000\u5C45\u6B21\u8981\u4F4D\u7F6E\u3002\u5C3D\u7BA1\u771F\u5B9E\u6027\u548C\u7B80\u660E\u6027\u662F\u65B0\u95FB\u7684\u6838\u5FC3\uFF0C\u5374\u9010\u6E10\u6210\u4E3A\u4E86\u966A\u886C\u3002</p><p>\u65B0\u95FB\u5B66\u662F\u4E00\u95E8\u6781\u5176\u91CD\u8981\u7684\u5B66\u79D1\u3002\u4E2D\u56FD\u5730\u57DF\u5E7F\u5927\uFF0C\u4E8B\u4EF6\u4F17\u591A\uFF0C\u4E0D\u662F\u6BCF\u4E2A\u4EBA\u90FD\u5177\u5907\u5728\u7F51\u7EDC\u4E0A\u53D1\u58F0\u7684\u80FD\u529B\uFF0C\u4E5F\u4E0D\u662F\u6BCF\u4E2A\u4EBA\u90FD\u80FD\u4E89\u53D6\u5230\u5E94\u6709\u7684\u6743\u5229\u3002\u901A\u8FC7\u91C7\u8BBF\u62A5\u9053\uFF0C\u4E3A\u6C11\u4F17\u53D1\u58F0\uFF0C\u5173\u6CE8\u4EBA\u6C11\u751F\u6D3B\u6240\u9700\uFF0C\u5E76\u901A\u8FC7\u5A92\u4F53\u8BC4\u8BBA\u8FDB\u884C\u793E\u4F1A\u76D1\u7763\u2014\u2014\u76D1\u7763\u6743\u529B\u3001\u63ED\u9732\u4E11\u6076\u3002\u8FD9\u662F\u4E00\u9879\u4F55\u7B49\u91CD\u8981\u7684\u5DE5\u4F5C\u3002</p><p>\u7136\u800C\uFF0C\u5982\u4ECA\uFF0C\u65B0\u95FB\u5B66\u5DF2\u7ECF\u4E0D\u518D\u9002\u5408\u62A5\u8003\u3002</p><p>\u5F20\u96EA\u5CF0\u5217\u4E3E\u7684\u4E0D\u63A8\u8350\u62A5\u8003\u65B0\u95FB\u5B66\u7684\u539F\u56E0\u5DF2\u7ECF\u8BE6\u5C3D\u65E0\u9057\uFF0C\u4F46\u6211\u60F3\u989D\u5916\u8865\u5145\u4E00\u70B9\uFF1A\u6BD5\u4E1A\u540E\uFF0C\u5982\u679C\u771F\u7684\u4ECE\u4E8B\u65B0\u95FB\u5DE5\u4F5C\uFF0C\u5FC5\u987B\u5B66\u4F1A\u59A5\u534F\u826F\u5FC3\u3002</p><p>\u6211\u6E05\u695A\u5730\u8BB0\u5F97\uFF0C\u57282010\u5E74\uFF0C\u6BCF\u5929\u665A\u4E0A\uFF0C\u6211\u90FD\u4F1A\u89C2\u770B\u300A\u7126\u70B9\u8BBF\u8C08\u300B\u3002\u8FD9\u6863\u8282\u76EE\u901A\u8FC7\u8C03\u67E5\u8FFD\u8E2A\u548C\u9690\u6027\u91C7\u8BBF\uFF0C\u63ED\u9732\u793E\u4F1A\u4E0A\u7684\u8FDD\u6CD5\u72AF\u7F6A\u884C\u4E3A\uFF0C\u8BE6\u5B9E\u5730\u63ED\u9732\u4E86\u8BB8\u591A\u4E11\u6076\u4E8B\u4EF6\u548C\u516C\u6743\u529B\u7684\u6EE5\u7528\u3002\u66DD\u5149\u4E4B\u540E\uFF0C\u901A\u8FC7\u6574\u6539\u548C\u7EA0\u6B63\uFF0C\u98CE\u6C14\u5F97\u4EE5\u6539\u5584\u3002\u7136\u800C\uFF0C\u4E0D\u77E5\u4ECE\u4F55\u65F6\u8D77\uFF0C\u8FD9\u6863\u8282\u76EE\u5931\u53BB\u4E86\u5F53\u521D\u7684\u521D\u8877\uFF0C\u5185\u5BB9\u9010\u6E10\u6A21\u7CCA\uFF0C\u4ECE\u63ED\u9732\u4E11\u6076\u8F6C\u53D8\u4E3A\u5343\u7BC7\u4E00\u5F8B\u7684\u7F8E\u597D\u5BA3\u4F20\u3002\u73B0\u5982\u4ECA\uFF0C\u867D\u7136\u7F51\u7EDC\u4E0A\u4ECD\u53EF\u627E\u5230\u5341\u591A\u5E74\u524D\u7684\u300A\u7126\u70B9\u8BBF\u8C08\u300B\u5185\u5BB9\uFF0C\u4F46\u5B83\u5DF2\u5931\u53BB\u4E86\u5F53\u521D\u7684\u9510\u6C14\uFF0C\u4ECE\u53CA\u65F6\u56DE\u5E94\u7FA4\u4F17\u5173\u5207\u53D8\u5F97\u65E0\u4EBA\u95EE\u6D25\u3002</p><p>\u6709\u4E9B\u8BDD\u60F3\u8BF4\u51FA\u53E3\uFF0C\u5374\u53C8\u54BD\u4E86\u56DE\u53BB\uFF0C\u53EA\u5269\u4E0B\u4E00\u58F0\u53F9\u606F\u3002</p><p>\u5982\u4ECA\uFF0C\u5A92\u4F53\u6240\u4F20\u8FBE\u7684\u5927\u90E8\u5206\u5185\u5BB9\u90FD\u662F\u300C\u53EA\u6709\u5BA3\u4F20\uFF0C\u65E0\u6709\u65B0\u95FB\uFF1B\u53EA\u6709\u7ACB\u573A\uFF0C\u65E0\u6709\u4E8B\u5B9E\u300D\u3002</p><p>\u56DE\u987E\u65E2\u5F80\u7684\u65B0\u95FB\uFF0C\u90A3\u4E9B\u65F6\u4E8B\u70ED\u70B9\u548C\u793E\u4F1A\u5B9E\u8DF5\u7684\u62A5\u9053\uFF0C\u5B9E\u9645\u4E0A\u5DF2\u7ECF\u65E0\u6CD5\u79F0\u4E4B\u4E3A\u65B0\u95FB\uFF0C\u53EA\u4E0D\u8FC7\u662F\u4E00\u5219\u901A\u77E5\uFF0C\u544A\u8BC9\u4F60\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u7136\u540E\u5F80\u5F80\u6CA1\u6709\u540E\u7EED\u3002\u8FC7\u53BB\uFF0C\u8BFB\u8005\u8FD8\u53EF\u4EE5\u5BF9\u65B0\u95FB\u53D1\u8868\u8BC4\u8BBA\uFF0C\u884C\u4F7F\u76D1\u7763\u7684\u6743\u529B\uFF0C\u8868\u8FBE\u81EA\u5DF1\u7684\u770B\u6CD5\u3002\u4F46\u5982\u4ECA\u4E0D\u540C\u4E86\uFF0C\u8BC4\u8BBA\u533A\u4E00\u5173\uFF0C\u8033\u6735\u4E00\u5835\uFF0C\u53EA\u5269\u4E0B\u81EA\u8BF4\u81EA\u8BDD\u3001\u60CA\u9669\u8BC4\u8BBA\u3001\u81EA\u5BFC\u81EA\u6F14\uFF0C\u5728\u81EA\u5DF1\u7684\u4E16\u754C\u91CC\u6C89\u8FF7\u4E0D\u5DF2\u3002\u5BF9\u4E8E\u4E00\u4E9B\u8D5E\u6B4C\u548C\u65E0\u5173\u7D27\u8981\u7684\u7410\u4E8B\uFF0C\u5B83\u4EEC\u5360\u636E\u4E86\u5927\u91CF\u7684\u5934\u6761\u65B0\u95FB\uFF0C\u770B\u4F3C\u5FD9\u5FD9\u788C\u788C\uFF0C\u5B9E\u5219\u6BEB\u65E0\u610F\u4E49\u3002</p><p>\u660E\u671D\u6709\u4E2A\u5927\u592A\u76D1\u53EB\u9B4F\u5FE0\u8D24\u3002\u5F53\u65F6\u7684\u7687\u5E1D\u7231\u5F53\u6728\u5320\uFF0C\u4E0D\u600E\u4E48\u7BA1\u4E8B\u3002\u9B4F\u5FE0\u8D24\u6743\u503E\u671D\u91CE\uFF0C\u6392\u9664\u5F02\u5DF1\uFF0C\u638C\u63E1\u751F\u6740\u5927\u6743\u3002\u5FA1\u53F2\u6768\u6D9F\u4E0E\u5176\u4ED6\u8C0F\u81E3\u8054\u5408\u5F39\u52BE\u9B4F\u5FE0\u8D24\u4E8C\u5341\u56DB\u9879\u7F6A\u72B6\uFF0C\u7687\u5E1D\u770B\u5230\u4E86\u6768\u6D9F\u7684\u594F\u7AE0\uFF0C\u9B4F\u5FE0\u8D24\u5FC3\u60F3\u81EA\u5DF1\u5B8C\u86CB\u4E86\u3002\u7136\u800C\uFF0C\u7687\u5E1D\u4E0D\u8BC6\u5B57\uFF0C\u8BA9\u9B4F\u5FE0\u8D24\u4EB2\u81EA\u8BFB\u51FA\u6765\u3002\u9B4F\u5FE0\u8D24\u9AD8\u5174\u574F\u4E86\uFF0C\u53EA\u6311\u4E86\u4E00\u4E9B\u65E0\u5173\u75DB\u75D2\u7684\u6761\u6B3E\u6765\u5FF5\uFF0C\u8FD8\u98A0\u5012\u9ED1\u767D\u3002\u7687\u5E1D\u542C\u4E86\u89C9\u5F97\u9B4F\u516C\u516C\u8FD8\u4E0D\u9519\uFF0C\u4E8E\u662F\u9B4F\u5FE0\u8D24\u53C8\u989D\u5916\u6DFB\u52A0\u4E86\u4E00\u4E9B\u65E0\u5173\u7D27\u8981\u7684\u5185\u5BB9\uFF0C\u6BD4\u5982\u5B98\u5458\u7684\u6843\u8272\u4E11\u95FB\u3001\u4EAC\u57CE\u7684\u5A31\u4E50\u6D3B\u52A8\u7B49\u7B49\u3002\u7687\u5E1D\u7684\u6CE8\u610F\u529B\u88AB\u8F6C\u79FB\u4E86\uFF0C\u9B4F\u5FE0\u8D24\u56E0\u6B64\u4FDD\u4F4F\u4E86\u4E00\u6761\u547D\u3002</p><p>\u8FD9\u4E2A\u6545\u4E8B\u63ED\u793A\u4E86\u5BA3\u4F20\u7684\u91CD\u8981\u6027\u3002\u4F20\u7EDF\u6587\u5316\u535A\u5927\u7CBE\u6DF1\uFF0C\u7A0D\u52A0\u6539\u6362\u51E0\u4E2A\u540C\u4E49\u8BCD\uFF0C\u98A0\u5012\u8BED\u53E5\u7684\u524D\u540E\u6B21\u5E8F\uFF0C\u5C31\u80FD\u4F7F\u4E8B\u5B9E\u7684\u6027\u8D28\u53D1\u751F\u5DE8\u5927\u53D8\u5316\u3002</p><p>\u9B4F\u5FE0\u8D24\u7684\u8FD9\u4E00\u5957\u624B\u6BB5\u5982\u4ECA\u88AB\u5A92\u4F53\u5B66\u4F1A\u4E86\uFF0C\u5F53\u9762\u5BF9\u4E0D\u5229\u4E8E\u81EA\u8EAB\u7684\u4E8B\u4EF6\uFF0C\u6216\u8005\u786C\u8981\u548C\u6C11\u4F17\u5531\u53CD\u8C03\u65F6\uFF0C\u4ED6\u4EEC\u5C31\u4F1A\u65BD\u5C55\u8FD9\u4E00\u4F0E\u4FE9\uFF0C\u767E\u8BD5\u767E\u7075\uFF0C\u5C61\u8BD5\u4E0D\u723D\u3002\u5982\u679C\u8FDE\u8FD9\u62DB\u4E5F\u4E0D\u7075\u4E86\uFF0C\u4ED6\u4EEC\u8FD8\u53EF\u4EE5\u7CBE\u9009\u8BC4\u8BBA\u3002</p><p>\u51E0\u4E2A\u6708\u524D\uFF0CChatGPT\u7684\u8BDD\u9898\u5F15\u8D77\u4E86\u8F70\u52A8\uFF0C\u4EBA\u5DE5\u667A\u80FD\u7684\u80FD\u529B\u4F17\u591A\uFF0C\u65E0\u8BBA\u662F\u4EC0\u4E48\u7A3F\u5B50\u90FD\u53EF\u4EE5\u5199\u3002\u56E0\u6B64\uFF0C\u6709\u4EBA\u9884\u6D4B\uFF0C\u5F53\u4EBA\u5DE5\u667A\u80FD\u666E\u53CA\u4E4B\u540E\uFF0C\u65B0\u95FB\u884C\u4E1A\u5C06\u662F\u6700\u5148\u88AB\u53D6\u4EE3\u7684\u3002\u6BD5\u7ADF\uFF0C\u65B0\u95FB\u5F3A\u8C03\u5B9E\u65F6\u5BA2\u89C2\uFF0C\u53EA\u6709\u51E0\u4E2A\u8981\u7D20\uFF0C\u4EBA\u5DE5\u667A\u80FD\u7684\u64B0\u7A3F\u901F\u5EA6\u80AF\u5B9A\u65E0\u6CD5\u4E0E\u4E4B\u76F8\u6BD4\u3002</p><p>\u7136\u800C\uFF0C\u6211\u5BF9\u6B64\u6301\u6709\u5F02\u8BAE\u3002\u9996\u5148\uFF0C\u6211\u4EEC\u7684\u65B0\u95FB\u5E76\u975E\u5E7F\u4E49\u4E0A\u7684\u65B0\u95FB\u3002\u9664\u4E86\u771F\u5B9E\u3001\u7B80\u660E\u548C\u53CA\u65F6\uFF0C\u6211\u4EEC\u8FD8\u5F3A\u8C03\u9009\u62E9\u6027\uFF0C\u66F4\u5F3A\u8C03\u610F\u8BC6\u5F62\u6001\uFF0C\u6625\u79CB\u7B14\u6CD5\uFF0C\u5C41\u80A1\u7ACB\u573A\u3002\u81F3\u4E8E\u5BA2\u89C2\u4E8B\u5B9E\uFF0C\u5B83\u53EA\u662F\u6B21\u8981\u7684\u914D\u89D2\u3002\u8FD9\u4E9B\u662F\u4EBA\u5DE5\u667A\u80FD\u65E0\u6CD5\u771F\u6B63\u638C\u63E1\u7684\uFF0C\u5B83\u4EEC\u662F\u6211\u4EEC\u72EC\u6709\u7684\u7279\u8272\u548C\u4F18\u52BF\u3002\u300C\u65B0\u95FB\u5DF2\u6B7B\u300D\u8FD9\u4E2A\u6807\u9898\u53EA\u662F\u7B80\u6D01\u5730\u8868\u8FBE\u4E86\u89C2\u70B9\uFF0C\u7F3A\u4E4F\u53CA\u65F6\u6027\u548C\u771F\u5B9E\u6027\u3002\u867D\u7136\u5929\u6C14\u9884\u62A5\u7684\u771F\u5B9E\u6027\u901A\u5E38\u662F\u53EF\u4FE1\u7684\uFF0C\u4F46\u6211\u4EEC\u53EA\u80FD\u76F8\u4FE1\uFF0C\u603B\u4E0D\u80FD\u6BCF\u5929\u8BB0\u5F55\u4E91\u5C42\u53D8\u5316\u548C\u7A7A\u6C14\u6E7F\u5EA6\u6765\u5224\u65AD\u5929\u6C14\u3002\u5373\u4F7F\u4ECA\u5929\u7684\u5929\u6C14\u9884\u62A5\u5DF2\u7ECF\u6B7B\u4EA1\uFF0C\u90A3\u4E5F\u65E0\u59A8\uFF0C\u6211\u4EEC\u53EF\u4EE5\u62A5\u6628\u5929\u7684\u5929\u6C14\u3002\u867D\u7136\u5931\u53BB\u4E86\u53CA\u65F6\u6027\uFF0C\u4F46\u81F3\u5C11\u4FDD\u7559\u4E86\u771F\u5B9E\u6027\uFF0C\u8FD9\u7B97\u5F97\u4E0A\u662F\u7A33\u59A5\u7684\u505A\u6CD5\u3002</p><p>\u4E5F\u6216\u8BB8\uFF0C\u6709\u4E9B\u8BDD\u60F3\u8981\u8BF4\u51FA\u53E3\uFF0C\u4F46\u6700\u7EC8\u8FD8\u662F\u54BD\u4E86\u56DE\u53BB\uFF0C\u53EA\u7559\u4E0B\u4E00\u58F0\u53F9\u606F\u3002</p>',16),o=[_];function d(n,r,c,i,h,l){return p(),a("div",null,o)}const T=e(s,[["render",d]]);export{m as __pageData,T as default};
